import React, { useState, useRef } from 'react';

import './styles.scss';
import { Search } from 'react-bootstrap-icons';
import { InputGroup, FormControl, Button, Form, Tab, Nav } from 'react-bootstrap';
class Detail extends React.Component {
    state = {};
    constructor(props) {
        super(props);

        // Use the Data array as the initial state
        this.state = {
            data: [
                {
                    id: "123",
                    name: "thanh thuy",
                    birthday: "20/12/2002",
                    phone: "154625321469",
                    email: "thuy@gmail.com",
                    address: "32 cgs dhdg dhđ dhy",
                    cccd: "4145145214",
                    chucvu: "nhân viên",
                    status: "hoạt động"
                },
                {
                    id: "321",
                    name: "thanh thuy 2",
                    birthday: "20/12/2002",
                    phone: "154625321469",
                    email: "thuy@gmail.com",
                    address: "32 cgs dhdg dhđ dhy",
                    cccd: "4145145214",
                    chucvu: "nhân viên",
                    status: "hoạt động"
                },
                {
                    id: "258",
                    name: "thanh thuy 3",
                    birthday: "20/12/2002",
                    phone: "154625321469",
                    email: "thuy@gmail.com",
                    address: "32 cgs dhdg dhđ dhy",
                    cccd: "4145145214",
                    chucvu: "nhân viên",
                    status: "hoạt động"
                }
            ],
            currentPage: 1,
            itemsPerPage: 2,
            selectedCustomerId: null,
        };
    }
    handleClick = (pageNumber) => {
        this.setState({
            currentPage: pageNumber,
        });
    };
    handleClickIdCustomer = (customerId) => {
        this.setState({
            selectedCustomerId: customerId,
        });
    };
    openModal = () => {
        this.setState({
            showModal: true,

        });
        console.log("aaaa");
    };

    closeModal = () => {
        this.setState({
            showModal: false,
        });
    };

    openDropdown = () => {
        this.setState({
            showDropdown: true,

        });
    };

    closeDropdown = () => {
        this.setState({
            showDropdown: false,
        });
    };

    render() {
        const { data, currentPage, itemsPerPage, selectedCustomerId } = this.state;

        // Calculate the index range for the current page
        const indexOfLastItem = currentPage * itemsPerPage;
        const indexOfFirstItem = indexOfLastItem - itemsPerPage;
        const currentItems = data.slice(indexOfFirstItem, indexOfLastItem);
        const totalPages = Math.ceil(data.length / itemsPerPage);
        const selectedCustomer = data.find(item => item.id === selectedCustomerId);
        const renderPageButtons = () => {
            const pageButtons = [];

            // Display all buttons if there are few pages
            if (totalPages <= 7) {
                for (let i = 1; i <= totalPages; i++) {
                    pageButtons.push(
                        <button className='p-2 px-3 fs-4 border border-1 me-2'
                            key={i}
                            onClick={() => this.handleClick(i)}
                            style={{ fontWeight: i === currentPage ? 'bold' : 'normal' }}
                        >
                            {i}
                        </button>
                    );
                }
            } else {
                // Display a subset of buttons with ellipsis
                const startPage = Math.max(1, currentPage - 2);
                const endPage = Math.min(totalPages, startPage + 4);

                if (startPage > 1) {
                    pageButtons.push(<span key="startEllipsis">...</span>);
                }

                for (let i = startPage; i <= endPage; i++) {
                    pageButtons.push(
                        <button className='p-2 fs-4 px-3  border border-1 me-2'
                            key={i}
                            onClick={() => this.handleClick(i)}
                            style={{ fontWeight: i === currentPage ? 'bold' : 'normal' }}
                        >
                            {i}
                        </button>
                    );
                }
                if (endPage < totalPages) {
                    pageButtons.push(<span key="endEllipsis">...</span>);
                }
            }
            return pageButtons;
        };
        return (
            <div className='container'>
                <div className='row ms-3 mt-4'>
                    <div className='card border border-2 rounded-3'>
                        <div className='card-content p-4'>
                            <div className='row'>
                                <div className='col-3 text-center'>
                                    <img src='https://demoda.vn/wp-content/uploads/2022/08/hinh-anh-avatar-nu-de-thuong.jpg' style={{ width: "150px" }}></img>
                                    <div className='mt-3 fs-5'>
                                        Mã nhân viên: 123
                                    </div>
                                </div>
                                <div className='col-3 px-4'>
                                    <div className="col-md-12 mb-4">
                                        <label for="name" className="form-label fs-5">Tên nhân viên :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.name || ''} />
                                        <div className="invalid-feedback">
                                            Tên nhân viên
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Số điện thoại :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.phone || ''} />
                                        <div className="invalid-feedback">
                                            Số điện thoại
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Ngày sinh :</label>
                                        <input type="date" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.birthday || ''} />
                                        <div className="invalid-feedback">
                                            Ngày sinh
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Email :</label>
                                        <input type="email" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.email || ''} />
                                        <div className="invalid-feedback">
                                            Email
                                        </div>
                                    </div>
                                </div>
                                <div className='col-3 px-4'>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Địa chỉ :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.address || ''} />
                                        <div className="invalid-feedback">
                                            Số nhà
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Tỉnh/ Thành phố :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" />
                                        <div className="invalid-feedback">
                                            Tỉnh/ Thành phố
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Quận/ Huyện/ Thị xã :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" />
                                        <div className="invalid-feedback">
                                            Quận/ Huyện/ Thị xã
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Xã/ Phường/ Thị trấn :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" />
                                        <div className="invalid-feedback">
                                            Xã/ Phường/ Thị trấn
                                        </div>
                                    </div>
                                </div>
                                <div className='col-3 px-4'>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">CMND/CCCD :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.cccd || ''} />
                                        <div className="invalid-feedback">
                                            CMND/CCCD
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Chức vụ :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.chucvu || ''} />
                                        <div className="invalid-feedback">
                                            Chức vụ
                                        </div>
                                    </div>
                                    <div className="col-md-12 mb-4">
                                        <label className="form-label fs-5">Trạng thái :</label>
                                        <input type="text" className="form-control p-2 rounded-3 fs-5" value={selectedCustomer?.status || ''} />
                                        <div className="invalid-feedback">
                                            Trạng thái
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className='mt-4 px-3'>
                    <div className='row my-3'>
                        <div className='col-3'>
                            <div className="form-group">
                                <label>
                                    <h5 >Tìm kiếm</h5>
                                </label>
                                <InputGroup className="w-100 ms-3">

                                    <FormControl className='p-3 fs-5'
                                        placeholder="Tên/ Số điện thoại"
                                        style={{ height: '100%' }}
                                        aria-label="Light"
                                        aria-describedby="basic-addon2"
                                    />
                                    <InputGroup.Text id="basic-addon2" className='p-3'>
                                        <Search />
                                    </InputGroup.Text>
                                </InputGroup>
                            </div>
                        </div>
                        <div className='col-2 ms-5'>
                            <div className="form-group">
                                <label>
                                    <h5 >Trạng thái</h5>
                                </label>
                                <select className="form-control p-3 fs-5 ms-3 select2-hidden-accessible" required
                                >
                                    <option disabled selected className='fs-5'>Chọn trạng thái</option>
                                    <option value="1" className='fs-5'>Hoạt động</option>
                                    <option value="2" className='fs-5'>Không hoạt động</option>

                                </select>

                            </div>
                        </div>
                    </div>

                    <table className='table  table-hover table-bordered fs-5  mt-2'>
                        <thead>
                            <tr className="table-secondary">
                                <th scope="col" className='p-3'>Mã NV</th>
                                <th scope="col" className='p-3'>Tên</th>
                                <th scope="col" className='p-3'>Năm sinh</th>
                                <th scope="col" className='p-3'>Số điện thoại</th>
                                <th scope="col" className='p-3'>Email</th>
                                <th scope="col" className='p-3'>Địa chỉ</th>
                                <th scope="col" className='p-3'>CCCD/CMND</th>
                                <th scope="col" className='p-3'>Chức vụ</th>
                                <th scope="col" className='p-3'>Trạng thái</th>
                                <th scope="col" className='p-3'></th>
                            </tr>
                        </thead>
                        <tbody>
                            {currentItems.map(item => (
                                <tr key={item.id} onClick={() => this.handleClickIdCustomer(item.id)}>
                                    <td className='p-3'>{item.id}</td>
                                    <td className='p-3'>{item.name}</td>
                                    <td className='p-3'>{item.birthday}</td>
                                    <td className='p-3'>{item.phone}</td>
                                    <td className='p-3'>{item.email}</td>
                                    <td className='p-3'>{item.address}</td>
                                    <td className='p-3'>{item.cccd}</td>
                                    <td className='p-3'>{item.chucvu}</td>
                                    <td className='p-3'>{item.status}</td>
                                    <td className='p-3 text-center'><i class="fa-solid fa-eye"></i></td>
                                </tr>
                            ))}

                        </tbody>
                    </table>
                    <div className='d-flex justify-content-center pt-3'>
                        {renderPageButtons()}
                    </div>
                </div>
                <div className="fixed-action-btn">
                    <button type="button"
                        className="btn btn-primary btn-lg rounded-circle" >
                        <i class="fas fa-bars fa-2x p-1 py-2" aria-hidden="true"></i>
                    </button>
                    <div className="dropdown">
                        <button className="btn btn-success rounded-circle">
                            <i class="fas fa-plus fa-2x  py-1" aria-hidden="true"></i>
                        </button>
                        <button className="btn btn-danger rounded-circle">
                            <i class="fas fa-trash fa-2x  py-1" aria-hidden="true"></i>
                        </button>
                    </div>
                </div>

            </div>
        );
    }
}

export default Detail;
